const API_URL = 'https://api.coincap.io/v2/assets';
const CRYPTO_SYMBOLS = ['bitcoin', 'ethereum', 'tether', 'usd-coin', 'binance-coin', 'cardano', 'xrp', 'dogecoin', 'litecoin'];

